mapper.insert:
insert into product(pd_name, pd_price, brand_id, pd_category,created_who ,created_at, count_love, product_img)
values ( #{Product.pd_name}, #{Product.pd_price}, #{Product.brand_id}, #{Product.pd_category}, #{Product.created_who}
       ,#{Product.created_at}, 0, #{Product.product_img})

mapper.update:
update product set pd_name=#{Product.pd_name},
pd_price=#{Product.pd_price},
pd_category=#{Product.pd_category},
count_love=#{Product.count_love}  
where product_id=#{Product.product_id}

mapper.select:
select * from product
order by product_id
limit #{size} offset #{offset}

select * from product
order by pd_price asc
limit #{size} offset #{offset}

mapper.update:
